import { Component, OnDestroy, OnInit } from '@angular/core';
import { FormBuilder, Validators, FormControl } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { UserService } from './services/user.service';
import { Transaction, UserInterface } from './models/user.model';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.scss'],
})
export class UserComponent implements OnInit, OnDestroy {
  public transactionForm = this.fb.group({
    amount: new FormControl('', [
      Validators.required,
      Validators.pattern('^[0-9]*$'),
    ]),
    detail: new FormControl('', [Validators.required]),
  });
  public data: UserInterface = null;
  private obs$: Subscription[] = [];
  constructor(
    private fb: FormBuilder,
    private activatedRoute: ActivatedRoute,
    private userService: UserService
  ) {}

  ngOnInit(): void {
    this.data = this.activatedRoute.snapshot.data['user'];
    console.log(this.data);
  }
  ngOnDestroy(): void {
    this.obs$.forEach((o) => o.unsubscribe());
  }

  public onSubmit() {
    const post = {
      detail: this.transactionForm.value.detail,
      amount: this.transactionForm.value.amount,
    };
    const o$ = this.userService.createTransaction(post).subscribe((postRes) => {
      const ntransaction: Transaction = {
        amount:postRes.amount,
        detail:postRes.detail,
        created:postRes.created,
        id:postRes.id,
        updated:postRes.updated,
      }
      this.data.data.transactions.push(ntransaction)
    });
    this.obs$.push(o$);
  }
}
<div class="container mt-4">
  <div class="row">
    <div class="col-lg-12 col-xl-8 mx-auto">
      <div class="card border-0 shadow rounded-3 my-5">
        <div class="card-body p-4 p-sm-5">
          <div class="d-flex align-items-center justify-content-between mb-5">
            <h5 class="card-title text-start fw-light fs-5">
              Welcome back Mr. FullStack User!
              <span class="badge bg-success ms-0"
                >Expenses: {{ data.totals.amount | currency : "USD" }}</span
              >
            </h5>
            <app-logout></app-logout>
          </div>
          <div class="mb-4">
            <form [formGroup]="transactionForm" (ngSubmit)="onSubmit()">
              <div class="row justify-content-center align-items-center">
                <div class="col">
                  <input
                    type="text"
                    placeholder="Detail"
                    id="detail"
                    formControlName="detail"
                    class="form-control"
                  />
                </div>
                <div class="col-4">
                  <input
                    type="text"
                    id="amount"
                    placeholder="Amount"
                    formControlName="amount"
                    class="form-control"
                  />
                </div>
                <div class="col-auto text-end">
                  <button
                    type="submit"
                    class="btn btn-dark"
                    [disabled]="!transactionForm.valid"
                  >
                    Buy
                  </button>
                </div>
              </div>
            </form>
          </div>
          <table class="table">
            <thead>
              <tr class="table-dark">
                <th class="text-start" scope="col">Transaction Detail</th>
                <th class="text-center" scope="col">Created</th>
                <th class="text-end" scope="col">Amount ($)</th>
              </tr>
            </thead>
            <tbody>
              <tr
                class="table-light"
                *ngFor="let transaction of data.data.transactions"
              >
                <td class="text-start">{{ transaction.detail }}</td>
                <td class="text-center">
                  {{ transaction.created | date : "dd/MM/yyyy" }}
                </td>
                <td class="text-end">
                  {{ transaction.amount | currency : "USD" }}
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </div>
</div>
