import { Component, OnDestroy, OnInit } from '@angular/core';
import { FormBuilder, Validators, FormControl } from '@angular/forms';
import {
  Router,
  ActivatedRouteSnapshot,
  ActivatedRoute,
} from '@angular/router';
import { LoginService } from './services/login.service';
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent implements OnInit, OnDestroy {
  public loginForm = this.fb.group({
    username: new FormControl('', [Validators.required]),
    password: new FormControl('', [Validators.required]),
  });
  public asyncError: string = '';
  private obs$ = [];
  constructor(
    private fb: FormBuilder,
    private routeService: Router,
    private loginService: LoginService,
    private activatedRoute: ActivatedRoute
  ) {}

  ngOnInit(): void {
    const d = this.activatedRoute.snapshot.data;
  }
  ngOnDestroy(): void {}
  public onSubmit() {
    // Validate login against API
    let password = this.loginForm.value.password;
    let username = this.loginForm.value.username;
    const auth$ = this.loginService.login({ username, password }).subscribe({
      next: (res) => {
        console.log(res);
        if (res.auth && res.user && res.user.role) {
          if (res.user.role.type === 'admin') {
            this.routeService.navigate(['/admin']);
          } else if (res.user.role.type === 'customer') {
            this.routeService.navigate(['/user']);
          }
        }
      },
      error: (err) => {
        console.log(err);
        this.asyncError = 'Bad credentials';
      },
    });
    this.obs$.push(auth$);
  }
}
<div class="container">
  <div class="row">
    <div class="col-sm-9 col-md-7 col-lg-5 mx-auto">
      <div class="card border-0 shadow rounded-3 my-5">
        <div class="card-body p-4 p-sm-5">
          <h5 class="card-title text-center mb-5 fw-light fs-5">
            Welcome Mr/s. Frontend!
          </h5>
          <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
            <!-- Email input -->
            <div class="form-outline mb-4">
              <label class="form-label" for="username">Username</label>
              <input
                type="text"
                id="username"
                formControlName="username"
                class="form-control"
              />
            </div>

            <div class="form-outline mb-4">
              <label class="form-label" for="password">Password</label>
              <input
                type="password"
                id="password"
                formControlName="password"
                class="form-control"
              />
            </div>

            <div class="alert alert-danger" role="alert" *ngIf="asyncError">
              Invalid credentials
            </div>
            <button
              type="submit"
              class="btn btn-primary"
              [disabled]="!loginForm.valid"
            >
              Sign in
            </button>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
