import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { tap } from 'rxjs/operators';
import { environment } from 'src/environments/environment';
import { Transaction, UserInterface } from '../models/user.model';

@Injectable()
export class UserService {
  apiRoot = '/user';

  constructor(private http: HttpClient) {}
  public getCurrentUser(): Observable<UserInterface> {
    return this.http.get<UserInterface>(environment.api.url + this.apiRoot);
  }
  public createTransaction({ amount, detail }): Observable<Transaction> {
    const endpoint = '/transaction';
    const body = {
      amount,
      detail,
    };
    return this.http.post<Transaction>(
      environment.api.url + this.apiRoot + endpoint,
      body
    );
  }
}
