import { Component, OnDestroy, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { FormBuilder, FormControl } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent implements OnInit, OnDestroy {
  public loginForm = this.fb.group({
    username: new FormControl(''),
    password: new FormControl(''),
  });
  public asyncError: boolean = false;
  constructor(private router: Router, private fb: FormBuilder) {}

  ngOnInit(): void {}
  ngOnDestroy(): void {}
  public onSubmit() {
    console.log(this.loginForm);
    // Submit post login to the API
    // Check asyncError on bad credentials
    // Redirect to the admin or user vew depending on role type
    let isadmin = true;
    if (isadmin) {
      this.router.navigate['/admin'];
    } else {
      this.router.navigate['/user'];
    }
  }
}
<div class="container">
  <div class="row">
    <div class="col-sm-9 col-md-7 col-lg-5 mx-auto">
      <div class="card border-0 shadow rounded-3 my-5">
        <div class="card-body p-4 p-sm-5">
          <h5 class="card-title text-center mb-5 fw-light fs-5">
            Welcome Mr. FullStack!
          </h5>
          <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
            <!-- Email input -->
            <div class="form-outline mb-4">
              <label class="form-label" for="username">Username</label>
              <input
                type="text"
                id="username"
                formControlName="username"
                class="form-control"
              />
            </div>

            <div class="form-outline mb-4">
              <label class="form-label" for="password">Password</label>
              <input
                type="password"
                id="password"
                formControlName="password"
                class="form-control"
              />
            </div>

            <div class="alert alert-danger" role="alert" *ngIf="asyncError">
              Invalid credentials
            </div>
            <button type="submit" class="btn btn-primary">Sign in</button>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
